mapper.insert:
INSERT INTO OISONE_COMMUNITY(
    COMMU_CODE
    , WRITER
    , TITLE
    , CONTENT
    , SECRET
    )VALUES(
      (SELECT 'CB_'||TO_CHAR((SYSDATE), 'YYMMDD')||'_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(COMMU_CODE, 11, 3))), 0)+1, 3, '0') AS COMMU_CODE  FROM OISONE_COMMUNITY)
      , #{writer}
      , #{title}
      , #{content}
      , #{secret}
)

INSERT INTO OISONE_COMMUNITY_REPLY(
	COMMU_REPLY_CODE
	, WRITER
	, CONTENT
	, COMMU_CODE
)VALUES(
	(SELECT 'CR_'||TO_CHAR((SYSDATE), 'YYMMDD')||'_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(COMMU_REPLY_CODE,11,3))), 0)+1 , 3, 0) 
    FROM OISONE_COMMUNITY_REPLY)
	 , #{writer}
	 , #{content}
	 , #{commuCode}
 )
