mapper.select:
SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE , ROW_NUM FROM( SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE , ROWNUM AS ROW_NUM FROM( SELECT CUSTOMER_CODE , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE FROM OISONE_CUSTOMER WHERE 1 = 1 AND IS_NOTICE = 'N' AND GROUP_CODE != 'GROUP_005'
AND ${searchKeyword} LIKE '%'||#{searchValue} ||'%'
ORDER BY CUSTOMER_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE , ROW_NUM FROM( SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE , ROWNUM AS ROW_NUM FROM( SELECT CUSTOMER_CODE , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE FROM OISONE_CUSTOMER WHERE 1 = 1 AND IS_NOTICE = 'Y'
AND ${searchKeyword} LIKE '%'||#{searchValue} ||'%'
ORDER BY CUSTOMER_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE , ROW_NUM FROM( SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE , ROWNUM AS ROW_NUM FROM( SELECT CUSTOMER_CODE , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , GROUP_CODE FROM OISONE_CUSTOMER WHERE 1 = 1 AND IS_NOTICE = 'N' AND WRITER = #{writer}
AND ${searchKeyword} LIKE '%'||#{searchValue} ||'%'
ORDER BY CUSTOMER_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.insert:
INSERT INTO OISONE_CUSTOMER(
    CUSTOMER_CODE 
    , WRITER 
    , TITLE 
    , CONTENT  
    , SECRET_NUMBER
    , IS_NOTICE
    , GROUP_CODE)
VALUES (
     #{customerCode}
     , #{writer}
     , #{title}
     , #{content}
     , #{secretNumber}
     , #{isNotice}
     , #{groupCode}
)

INSERT INTO OISONE_CUSTOMER_IMG(
     CUSTOMER_IMG_CODE
    , ORIGIN_IMG_NAME
    , ATTACHED_IMG_NAME
    , CUSTOMER_CODE
)

	SELECT #{imgInfo.customerImgCode}
		, #{imgInfo.originImgName}
		, #{imgInfo.attachedImgName}
		, #{imgInfo.customerCode}
	FROM DUAL

mapper.update:
UPDATE OISONE_CUSTOMER
SET TITLE = #{title}
    , CONTENT  = #{content}
    , SECRET_NUMBER = #{secretNumber}
    , IS_NOTICE = #{isNotice}
    , GROUP_CODE = #{groupCode}
WHERE CUSTOMER_CODE = #{customerCode}

mapper.insert:
		INSERT INTO OISONE_CUSTOMER_REPLY (
		    CUSTOMER_REPLY_CODE 
		    , WRITER 
		    , CONTENT
		    , CUSTOMER_CODE 
		)VALUES(
		    (SELECT 'CSR_'||TO_CHAR((SYSDATE), 'YYMMDD')||'_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CUSTOMER_REPLY_CODE,12,3))), 0)+1 , 3, 0) 
    		FROM OISONE_CUSTOMER_REPLY)
		    ,  #{writer}
		    , #{content}
		    , #{customerCode}
		)

