mapper.select:
SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , ROW_NUM FROM( SELECT CUSTOMER_CODE , CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE , ROWNUM AS ROW_NUM FROM( SELECT CUSTOMER_CODE , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE FROM OISONE_CUSTOMER WHERE 1 = 1 AND IS_NOTICE = 'N'
AND ${searchKeyword} LIKE '%'||#{searchValue} ||'%'
ORDER BY CUSTOMER_CODE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

SELECT CUSTOMER_CODE , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , WRITER , TITLE , CONTENT , IS_READ , SECRET_NUMBER , IS_NOTICE FROM OISONE_CUSTOMER WHERE 1 = 1 AND WRITER = #{writer}
AND ${searchKeyword} LIKE '%'||#{searchValue} ||'%'
AND IS_NOTICE = 'N' ORDER BY CUSTOMER_CODE DESC

mapper.insert:
INSERT INTO OISONE_CUSTOMER(
    CUSTOMER_CODE 
    , WRITER 
    , TITLE 
    , CONTENT  
    , SECRET_NUMBER
    , IS_NOTICE)
VALUES (
     #{customerCode}
     , #{writer}
     , #{title}
     , #{content}
     , #{secretNumber}
     , #{isNotice}
)

INSERT INTO OISONE_CUSTOMER_IMG(
     CUSTOMER_IMG_CODE
    , ORIGIN_IMG_NAME
    , ATTACHED_IMG_NAME
    , CUSTOMER_CODE
)

	SELECT #{imgInfo.customerImgCode}
		, #{imgInfo.originImgName}
		, #{imgInfo.attachedImgName}
		, #{imgInfo.customerCode}
	FROM DUAL
