mapper.insert:
		INSERT INTO OISONE_MEMBER(
		    ID
		    , PW
		    , NAME
		    , NICKNAME
		    , BIRTHDAY
		    , GENDER
		    , TELL
		    , EMAIL
		    , MARKETING_AGREE
		    , NONPEOPLE_TICKET
		    , MY_CINEMA

		)VALUES(
		    #{id}
		    , #{pw}
		    , #{name}
		    , #{nickName}
		    , #{birthday}
		    , #{gender}
		    , #{tell}
		    , #{email}
		    , #{marketingAgree}
		    , #{nonpeopleTicket}
		    , #{myCinema}

		)

mapper.select:
SELECT COUNT(*)
FROM OISONE_MEMBER
WHERE ID = #{id}

SELECT COUNT(*)
FROM OISONE_MEMBER
WHERE EMAIL = #{email}

SELECT COUNT(*)
FROM OISONE_MEMBER
WHERE NICKNAME = #{nickName}

SELECT COUNT(*)
FROM OISONE_MEMBER
WHERE ID = #{id}
AND PW = #{pw}

SELECT ID , NAME , NICKNAME , TELL , ROW_NUM FROM ( SELECT ID , NAME , NICKNAME , TELL , ROWNUM AS ROW_NUM FROM ( SELECT ID , NAME , NICKNAME , TELL , ROWNUM AS ROW_NUM FROM OISONE_MEMBER
WHERE ${searchKeyword} LIKE '%'||#{searchValue}||'%'
ORDER BY JOIN_DATE ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.update:
UPDATE OISONE_MEMBER
SET
 NAME = #{name}
  , NICKNAME = #{nickName}
  , BIRTHDAY = #{birthday}
  , GENDER = #{gender}
  , TELL = #{tell}
  , EMAIL = #{email}
  , NONPEOPLE_TICKET = #{nonpeopleTicket}
  , MY_CINEMA = #{myCinema}
 WHERE ID = #{id}

mapper.select:
SELECT COMMU_CODE , WRITER , CREATE_DATE , READ_CNT , TITLE , CONTENT , SECRET , ROW_NUM FROM( SELECT COMMU_CODE , WRITER , CREATE_DATE , READ_CNT , TITLE , CONTENT , SECRET , ROWNUM AS ROW_NUM FROM( SELECT COMMU_CODE , WRITER , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , READ_CNT , TITLE , CONTENT , SECRET , ROWNUM AS ROW_NUM FROM OISONE_COMMUNITY WHERE WRITER = #{nickName}
AND ${searchKeyword} LIKE '%'||#{searchValue}||'%'
ORDER BY CREATE_DATE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}
