mapper.insert:
INSERT INTO OISONE_MOVIE (
	MV_CODE
	, TITLE
	, DIRECTOR
	, ACTOR
	, GENRE
	, AGE
	, RUNNING_TIME
	, NATION
	, OPEN_DATE
	, CONTENT
	, MV_STATE
) VALUES (
	#{mvCode}
	, #{title}
	, #{director}
	, #{actor}
	, #{genre}
	, #{age}
	, #{runningTime}
	, #{nation}
	, #{openDate}
	, #{content}
	, #{mvState}
)

INSERT INTO OISONE_MOVIE_IMG (
	MOVIE_IMG_CODE
	, ORIGIN_IMG_NAME
	, ATTACHED_IMG_NAME
	, MV_CODE
) VALUES (
	#{movieImgCode}
	, #{originImgName}
	, #{attachedImgName}
	, #{mvCode}
)

INSERT INTO OISONE_MOVIE_REPLY (
	MV_REPLY_CODE
	, GRADE
	, WRITER
	, MV_REPLY_CONTENT
	, MV_CODE
) VALUES (
	(SELECT 'MV_'||TO_CHAR((SYSDATE), 'YYMMDD')||'_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MV_REPLY_CODE, 11, 3))), 0) + 1, 3, '0') FROM OISONE_MOVIE_REPLY)
	, #{grade}
	, #{writer}
	, #{mvReplyContent}
	, #{mvCode}
)
