mapper.select:
SELECT MOVIE.MV_CODE , TITLE , MOVIE.RUNNING_TIME , AGE FROM OISONE_MOVIE MOVIE, OISONE_MOVIE_TIME TIME WHERE MV_STATE = 'Y' AND MOVIE.MV_CODE = TIME.MV_CODE
AND AREA_NAME = #{areaName}
ORDER BY MV_CODE

mapper.insert:
		INSERT INTO OISONE_MOVIE_TIME(
		    MV_TIME_CODE
		    , THEATER_CODE
		    , AREA_NAME
		    , SCREEN_TIME
            , SCREEN_END_TIME
		    , SCREEN_DAY
		    , MV_CODE
		    , RUNNING_TIME
		) VALUES(
		    (SELECT 'MT_'||#{screenDay}||'_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MV_TIME_CODE,15,3))), 0)+1 , 3, 0)
		    FROM OISONE_MOVIE_TIME)
		    , #{theaterCode}
		    , #{areaName}
		    , #{screenTime}
            , #{screenEndTime}
		    , #{screenDay}
		    , #{mvCode}
		    , #{runningTime}
			)

mapper.select:
SELECT MV_TIME_CODE , SCREEN_DAY , SCREEN_TIME , SCREEN_END_TIME , MV_CODE , RUNNING_TIME , TIME.THEATER_CODE , SEAT_ALL+30 AS SEAT_ALL , SEAT_CNT , SEAT_ALL-SEAT_CNT+30 AS SEAT_LEFT , THEATER_NAME , IS_FULL_THEATER , (SELECT TITLE FROM OISONE_MOVIE MOVIE WHERE MOVIE.MV_CODE = TIME.MV_CODE) AS TITLE FROM OISONE_MOVIE_TIME TIME, OISONE_THEATER THEATER WHERE TIME.THEATER_CODE = THEATER.THEATER_CODE
AND TIME.AREA_NAME = #{areaName}
AND MV_CODE = #{mvCode}
AND SCREEN_DAY = #{screenDay} ORDER BY MV_TIME_CODE

mapper.insert:
INSERT INTO OISONE_RESERVATION (
	RES_CODE
	, ID
	, MV_TIME_CODE
	, SEAT_NAME
	, SEAT_CNT
	, TOTAL_PRICE
) VALUES (
	#{resCode}
	, #{id}
	, #{mvTimeCode}
	, #{seatName}
	, #{seatCnt}
	, #{totalPrice}
)

mapper.update:
UPDATE OISONE_THEATER
SET SEAT_CNT = #{seatCnt}
WHERE THEATER_CODE = #{theaterCode}
